mapper.select:
select record.uid, record.iid, name, song_name, record.weight, record.timestamp
from record,
     song,
     user
where record.uid = #{uid}
  and record.uid = user.uid
  and record.iid = #{iid}
  and record.iid = song.iid

mapper.update:
UPDATE record
SET weight=weight + 1,
    timestamp=#{timestamp}
WHERE uid = #{uid}
  AND iid = #{iid}
